import Titulo from "Components/Titulo"
import styles from "./Banner.module.css"
import db from "data/db.json"
import { useEffect, useState } from "react";

function Banner(){
    const [randomData, setRandomData] = useState({});

    useEffect(() => {
        const changeRandomData = () => {
            const randomIndex = Math.floor(Math.random() * db.length);
            setRandomData(db[randomIndex]);
        }

        const interval = setInterval(changeRandomData, 10000);

        return () => clearInterval(interval);
    }, []);
    
    const getRandomColor = () => {
        const colors = ["#6BD1FF", "#FF6B6B", "#FFD700", "#98FB98", "#FFA500"];
        const randomIndex = Math.floor(Math.random() * colors.length);
        return colors[randomIndex];
    }

    const categoriaStyle = {
        backgroundColor: getRandomColor()
    };

    const imagenStyle = {
        border: `2px solid ${getRandomColor()}`
    };

    return(
        <div className={styles.background}>
            <div className={styles.overlay}>
                <div>
                    <Titulo>
                        <div className={styles.categoria} style={categoriaStyle}>
                            <h1>{randomData.categoria}</h1>
                        </div>
                    </Titulo>
                    <Titulo>
                        <h2>{randomData.titulo}</h2>
                    </Titulo>
                    <p>{randomData.descripcion}</p>
                </div>
                <div>
                    <img src={randomData.capa} alt="Imagen del Player" className={styles.imagen} style={imagenStyle}/>
                </div>
            </div>
        </div>
    )
}

export default Banner
