import { useState } from "react"
import styles from "./NuevoVideo.module.css"

function NuevoVideo(){

    const [form, setForm] = useState({
        titulo: "",
        categoria: "",
        capa: "",
        
    })

    return(
        <>
        <form noValidate>
            <div className="mb-3">
                <label className="form-label">Título</label>
                <input
                    type="text"
                    className="form-control"
                    value={FormData.nombre}
                    onChange={(e) => updateForm({name: e.target.value})}
                    onBlur={() => validarInput('nombre')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Categoría</label>
                <input
                    type="text"
                    className="form-control"
                    value={FormData.categoria}
                    onChange={(e) => updateForm({name: e.target.value})}
                    onBlur={() => validarInput('categoria')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Imagen</label>
                <input
                    type="text"
                    className="form-control"
                    value={FormData.capa}
                    onChange={(e) => updateForm({name: e.target.value})}
                    onBlur={() => validarInput('capa')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Video</label>
                <input
                    type="text"
                    className="form-control"
                    value={FormData.videoURL}
                    onChange={(e) => updateForm({name: e.target.value})}
                    onBlur={() => validarInput('videoURL')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Descirpción</label>
                <input
                    type="text"
                    className="form-control"
                    value={FormData.descirpcion}
                    onChange={(e) => updateForm({name: e.target.value})}
                    onBlur={() => validarInput('descirpcion')}
                />
            </div>
            <button type="submit" className="btnGuardar">Guardar</button>
            <button type="submit" className="btnLimpiar">Limpiar</button>
        </form>
        </>
    )
}

export default NuevoVideo
