import styles from "./Tarjetas.module.css"
import iconoCerrar from "./CloseIcon.png"
import videos from "../"

function Tarjeta(id, titulo, link){
    return(
        <>
        <div className={styles.contenedor}>
            <img src={link} alt={titulo} className={styles.tarjeta}/>
            <h3>{titulo}</h3>
            <img src={iconoCerrar} alt="Ícono Favorito"/>
        </div>
        <section className={styles.contenedor}>
        {videos.map((video)=>{
            return <Tarjeta {...video} key={video.id}/>
        })}
    </section>
    </>
    )
}



export default Tarjeta
