import styles from "./Tarjetas.module.css"
// import iconoCerrar from "./CloseIcon.png"
import iconoEditar from "./EditIcon.png"
import { useState } from "react"
import BotonBorrar from "Components/BotonBorrar";

function Tarjeta({capa, videoURL}){
    const [reproduciendo, setReproduciendo] = useState(false);

    const manejandoElVideo = () => {
        setReproduciendo(true);
    }

    return(
        <section className={styles.contenedorGeneral}>
            <div className={styles.contenedorVideo}>
                {!reproduciendo ? (
                    <img
                        src={capa}
                        className={styles.imagen}
                        alt="Miniatura del Video"
                        onClick={manejandoElVideo}
                        style={{cursor: "pointer"}}
                    />
                ) : (
                    <iframe
                        className={styles.video}
                        src={videoURL}
                        title="Video"
                        frameBorder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowFullScreen
                    ></iframe>
                )}
            </div>
            <div className={styles.contenedorBotones}>
                <BotonBorrar/>
                <button className={styles.btnEditar}>
                    <img src={iconoEditar} alt="Ícono Editar"/>
                    <h3>EDITAR</h3>
                </button>
            </div>
        </section>
    )
}

export default Tarjeta
