import { Link, useLocation } from "react-router-dom"
import styles from "./HeaderLinks.module.css"
import iconMasAzul from "./IconoMasAzul.png"
import iconMasBlanco from "./IconoMasBlanco.png"
import iconHomeAzul from "./IconoHomeAzul.png"
import iconHomeBlando from "./IconoHomeBlanco.png"

function HeaderLinks(){
    const location = useLocation()

    return(
        <section className={styles.contenedorLinks}>
            <Link to="./" className={`${styles.linkHome} ${
          location.pathname === "/" ? styles.activeLink : ""
        }`}>
                <img src={iconHomeAzul} alt="Ícono Home Azul" className={styles.icoBlaHome}></img>
                <img src={iconHomeBlando} alt="ícono Home Blanco" className={sty}></img>
                <label>HOME</label>
            </Link>
            <Link to="./NuevoVideo" className={`${styles.linkHome} ${
          location.pathname === "/NuevoVideo" ? styles.activeLink : ""
        }`}>
                {/* <img src={iconMasAzul} alt="Ícono Más Azul"></img> */}
                <img src={iconMasBlanco} alt="ícono Más Blanco" className={styles.icoBlaMas}></img>
                <label>VIDEO NUEVO</label>
            </Link>
        </section>
    )
}

export default HeaderLinks
