// Components/Tarjeta.js
import React, { useState } from "react";
import styles from "./Tarjetas.module.css";
import BotonBorrar from "Components/BotonBorrar";
import BotonEditar from "Components/BotonEditar";
import Modal from "Components/Modal";

function Tarjeta({ video, categorias, onDelete, onSave }) {
    const [reproduciendo, setReproduciendo] = useState(false);
    const [editando, setEditando] = useState(false);
    const [editedTitle, setEditedTitle] = useState(video.title);
    const [editedDescription, setEditedDescription] = useState(video.description);

    const manejandoElVideo = () => {
        setReproduciendo(true);
    };

    const manejandoElBorrado = () => {
        onDelete(video.id);
    };

    const manejandoElEditado = () => {
        setEditando(true);
    };

    const manejandoElCierre = () => {
        setEditando(false);
    };

    const manejandoElSalvado = () => {
        onSave({ ...video, title: editedTitle, description: editedDescription });
        setEditando(false);
    };

    return (
        <section className={styles.contenedorGeneral}>
            <div className={styles.contenedorVideo}>
                {!reproduciendo ? (
                    <img
                        src={video.capa}
                        className={styles.imagen}
                        alt="Miniatura del Video"
                        onClick={manejandoElVideo}
                        style={{ cursor: "pointer" }}
                    />
                ) : (
                    <iframe
                        className={styles.video}
                        src={video.videoURL}
                        title="Video"
                        frameBorder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowFullScreen
                    ></iframe>
                )}
            </div>
            <div className={styles.contenedorBotones}>
                <BotonBorrar onDelete={manejandoElBorrado} />
                <BotonEditar onEdit={manejandoElEditado} />
            </div>
            {editando && (
                <Modal onClose={manejandoElCierre}>
                    <h2>Editar Video</h2>
                    <label>
                        Título:
                        <input
                            type="text"
                            value={editedTitle}
                            onChange={(e) => setEditedTitle(e.target.value)}
                        />
                    </label>
                    <label>
                        Descripción:
                        <textarea
                            value={editedDescription}
                            onChange={(e) => setEditedDescription(e.target.value)}
                        />
                    </label>
                    <button onClick={manejandoElSalvado}>Guardar Cambios</button>
                </Modal>
            )}
        </section>
    );
}

export default Tarjeta;
