import styles from "./NuevoVideo.module.css"
import { useForm } from "react-form-ease"

function NuevoVideo(){

    const {formData, updateForm, validateForm} = useForm({
        data: {
            titulo: "",
            categoria: "",
            capa: "",
            videoURL: "",
            descripcion:""
        },
        validations: {
            titulo: (value) => {
                if(!value) return 'Ingresa el nombre'
            }
        }
    })

    const manejandoElSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault()
        if(!validateForm()) return

        console.log(formData);
    }

    return(
        <>
        <form noValidate onSubmit={manejandoElSubmit}>
            <div className="mb-3">
                <label className="form-label">Título</label>
                <input
                    type="text"
                    className="form-control"
                    value={formData.titulo}
                    onChange={(e) => updateForm({titulo: e.target.value})}
                    onBlur={() => validarInput('titulo')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Categoría</label>
                <input
                    type="text"
                    className="form-control"
                    value={formData.categoria}
                    onChange={(e) => updateForm({categoria: e.target.value})}
                    onBlur={() => validarInput('categoria')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Imagen</label>
                <input
                    type="text"
                    className="form-control"
                    value={formData.capa}
                    onChange={(e) => updateForm({capa: e.target.value})}
                    onBlur={() => validarInput('capa')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Video</label>
                <input
                    type="text"
                    className="form-control"
                    value={formData.videoURL}
                    onChange={(e) => updateForm({videoURL: e.target.value})}
                    onBlur={() => validarInput('videoURL')}
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Descripción</label>
                <input
                    type="text"
                    className="form-control"
                    value={formData.descripcion}
                    onChange={(e) => updateForm({descripcion: e.target.value})}
                    onBlur={() => validarInput('descripcion')}
                />
            </div>
            <button type="submit" className="btnGuardar">Guardar</button>
            <button type="submit" className="btnLimpiar">Limpiar</button>
        </form>
        </>
    )
}

export default NuevoVideo
