import React, { useState } from "react"
import Banner from "Components/Banner"
import Tarjeta from "Components/Tarjetas"
import styles from "./index.module.css"
import videos from "data/db.json"

function Inicio() {

    const [videosR, setVideosR] = useState(videos);

    const [categoriaSeleccionada, setCategoriaSeleccionada] = useState("");

    const manejandoElBorrado = (id) => {
        setVideosR(videosR.filter((video) => video.id !== id));
    };

    const manejandoElSalvado = (updatedVideo) => {
        setVideosR(videosR.map((video) =>
            video.id === updatedVideo.id ? updatedVideo : video
        ));
    }

    const categorias = [...new Set(videosR.map((video) => video.categoria))];

    const videosFiltrados = videosR.filter(video => video.categoria === categoriaSeleccionada);

    return (
        <>
            <Banner />
            <div className={styles.filtroCategoria}>
                <label>
                    Seleccionar Categoría:
                    <select
                        value={categoriaSeleccionada}
                        onChange={(e) => setCategoriaSeleccionada(e.target.value)}
                    >
                        <option value="">Todas</option>
                        {categorias.map((cat) => (
                            <option key={cat} value={cat}>
                                {cat}
                            </option>
                        ))}
                    </select>
                </label>
            </div>
            <section className={styles.contenedor}>
                {categoriaSeleccionada && (
                    <div key={categoriaSeleccionada}>
                        <h2>{categoriaSeleccionada}</h2>
                        <div className={styles.videosPorCategoria}>
                            {videosFiltrados.map((video) => (
                                <Tarjeta
                                    key={video.id}
                                    video={video}
                                    categorias={categorias}
                                    onDelete={manejandoElBorrado}
                                    onSave={manejandoElSalvado}
                                />
                            ))}
                        </div>
                    </div>
                )}
            </section>
        </>
    );
}

export default Inicio;









