import Titulo from "Components/Titulo"
import styles from "./Banner.module.css"
// import Imagen from "./player.png"
import db from "data/db.json"
import { useEffect, useState } from "react";

function Banner(){
    const [randomData, setRandomData] = useState({});
    const colores = ["#6BD1FF", "#00C86F", "#FFBA05"];
    const coloresCategorias = {
        categoria1: "#6BD1FF",
        categoria2: "#00C86F",
        categoria3: "#0000FF",
        // Agrega más categorías y colores según tus necesidades
      };

    const [borderColor, setBorderColor] = useState(colores[Math.floor(Math.random() * colores.length)]);
    const [backgroundColor, setBackgroundColor] = useState(colores[Math.floor(Math.random() * colores.length)]);

    useEffect(() => {
        const changeRandomData = () => {
            const randomIndex = Math.floor(Math.random() * db.length);
            setRandomData(db[randomIndex]);
            setBorderColor(colores[Math.floor(Math.random() * colores.length)]);
            setBackgroundColor(colores[Math.floor(Math.random() * colores.length)]);
        }

        const interval = setInterval(changeRandomData, 10000);

        return () => clearInterval(interval);
    }, []);
    

    return(
        <div className={styles.background}>
            <div className={styles.overlay}>
                <div>
                    <Titulo>
                        <div className={styles.categoria} style={{ backgroundColor: backgroundColor }}>
                            <h1>{randomData.categoria}</h1>
                        </div>
                    </Titulo>
                    <Titulo>
                        <h2>{randomData.titulo}</h2>
                    </Titulo>
                    <p>{randomData.descripcion}</p>
                </div>
                <div>
                    <img src={randomData.capa} alt="Imagen del Player" className={styles.imagen} style={{ borderColor: borderColor }}/>
                </div>
            </div>
        </div>
    )
}

export default Banner
